import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ManageArtists = () => {
    const [artists, setArtists] = useState([]);

    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/artists');
                setArtists(response.data);
            } catch (err) {
                console.error('Error fetching artists:', err.response?.data || err.message);
            }
        };

        fetchArtists();
    }, []);

    const handleDelete = async (id) => {
        try {
            const token = localStorage.getItem('token'); // Retrieve token from localStorage
    
            console.log('Deleting artist with ID:', id); // Log the artist ID
            console.log('Token:', token); // Log the token being sent
    
            const response = await axios.delete(`/api/artists/${id}`, {
                headers: { Authorization: `Bearer ${token}` }, // Include token for authentication
            });
    
            console.log('Delete Response:', response.data); // Log the response
            alert('Artist deleted successfully!');
            setArtists((prevArtists) => prevArtists.filter((artist) => artist._id !== id)); // Update state
        } catch (err) {
            console.error('Error deleting artist:', err.response?.data || err.message); // Log the full error
            alert('Failed to delete artist. Please try again.');
        }
    };
    return (
        <div style={{ padding: '20px' }}>
            <h2>Manage Artists</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                <thead>
                    <tr style={{ backgroundColor: '#2c3e50', color: '#fff' }}>
                        <th style={{ padding: '10px' }}>Name</th>
                        <th style={{ padding: '10px' }}>Email</th>
                        <th style={{ padding: '10px' }}>Category</th>
                        <th style={{ padding: '10px' }}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {artists.map((artist) => (
                        <tr key={artist._id} style={{ borderBottom: '1px solid #ddd' }}>
                            <td style={{ padding: '10px' }}>{artist.name}</td>
                            <td style={{ padding: '10px' }}>{artist.email}</td>
                            <td style={{ padding: '10px' }}>{artist.category}</td>
                            <td style={{ padding: '10px' }}>
                                <Link
                                    to={`/admin/edit-artist/${artist._id}`}
                                    style={{
                                        marginRight: '10px',
                                        padding: '5px 10px',
                                        backgroundColor: '#3498db',
                                        color: '#fff',
                                        textDecoration: 'none',
                                        borderRadius: '5px',
                                    }}
                                >
                                    Edit
                                </Link>
                                <button
                                    onClick={() => handleDelete(artist._id)}
                                    style={{
                                        padding: '5px 10px',
                                        backgroundColor: '#e74c3c',
                                        color: '#fff',
                                        border: 'none',
                                        cursor: 'pointer',
                                        borderRadius: '5px',
                                    }}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageArtists;
