import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Artists.css'; // Import the CSS file for styling

const Artists = () => {
    const [artists, setArtists] = useState([]); // State to store artists
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    // Fetch artists from the backend
    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/artists'); // Replace with your API endpoint
                setArtists(response.data); // Set the fetched artists
                setLoading(false); // Stop loading
            } catch (err) {
                console.error('Error fetching artists:', err.response?.data || err.message);
                setError('Failed to load artists. Please try again later.');
                setLoading(false); // Stop loading on error
            }
        };

        fetchArtists(); // Call the function to fetch artists
    }, []); // Run once when the component mounts

    // Render loading state
    if (loading) {
        return <p style={{ textAlign: 'center', fontSize: '1.2rem' }}>Loading artists...</p>;
    }

    // Render error state
    if (error) {
        return <p style={{ textAlign: 'center', color: 'red', fontSize: '1.2rem' }}>{error}</p>;
    }

    return (
        <div className="artists-container">
            {/* Heading */}
            <h2 className="artists-heading">Уран бүтээлчид</h2>

            {/* Artist Cards */}
            {artists.length > 0 ? (
                artists.map((artist) => (
                    <div key={artist._id} className="artist-card">
                        <img
                            src={artist.profilePicture || '/default-avatar.png'} // Fallback image
                            alt={`${artist.name}'s profile`}
                            onError={(e) => {
                                e.target.src = '/default-avatar.png'; // Fallback image
                            }}
                        />
                        <h3>{artist.name}</h3>
                        
                        <p>{artist.bio || 'No bio available.'}</p>
                    </div>
                ))
            ) : (
                <p style={{ textAlign: 'center', fontSize: '1.2rem' }}>No artists found.</p>
            )}
        </div>
    );
};

export default Artists;
