import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { ThemeContext } from '../context/ThemeContext'; // Import ThemeContext
import './ArtistLogin.css'; // Import the external CSS file

const ArtistLogin = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { login } = useAuth(); // Use the login function from AuthContext
    const navigate = useNavigate();
    const { darkMode } = useContext(ThemeContext); // Access darkMode state

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/auth/artist/login', { email, password });
            const { token } = response.data;

            // Save token in context/localStorage
            login(token);

            // Redirect to artist dashboard
            navigate('/artist/dashboard');
        } catch (err) {
            setError(err.response?.data?.message || 'Login failed.');
        }
    };

    return (
        <div className={`login-container ${darkMode ? 'dark-mode' : ''}`}>
            <div className="login-card">
                <h2 className="login-heading">Artist Login</h2>
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleSubmit} className="login-form">
                    <div className="input-group">
                        <label htmlFor="email" className="input-label">Email</label>
                        <input
                            type="email"
                            id="email"
                            placeholder="Enter your email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="input-field"
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password" className="input-label">Password</label>
                        <input
                            type="password"
                            id="password"
                            placeholder="Enter your password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="input-field"
                        />
                    </div>
                    <button type="submit" className="login-button">
                        Login as Artist
                    </button>
                </form>
                <div className="login-link">
                    <a href="/forgot-password" className="login-anchor">
                        Forgot Password?
                    </a>
                </div>
            </div>
        </div>
    );
};

export default ArtistLogin;
