import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';

const ManageArtists = () => {
    const [artists, setArtists] = useState([]);
    const { token } = useAuth();

    // Fetch all artists using useCallback
    const fetchArtists = useCallback(async () => {
        try {
            const response = await axios.get('/api/admin/artists', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setArtists(response.data);
        } catch (err) {
            console.error('Error fetching artists:', err.response?.data || err.message);
        }
    }, [token]);

    useEffect(() => {
        fetchArtists();
    }, [fetchArtists]);

    // Delete an artist
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/admin/artists/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchArtists(); // Refresh the list
        } catch (err) {
            console.error('Error deleting artist:', err.response?.data || err.message);
        }
    };

    return (
        <div>
            <h2>Manage Artists</h2>
            <Link to="/admin/add-artist">
                <button>Add New Artist</button>
            </Link>

            {/* List of artists */}
            <ul>
                {artists.length > 0 ? (
                    artists.map((artist) => (
                        <li key={artist._id}>
                            <strong>{artist.name}</strong>: {artist.email}
                            <br />
                            <em>Bio:</em> {artist.bio || 'No bio'}
                            <br />
                            <em>Category:</em> {artist.category}
                            <br />
                            <img src={artist.profilePicture} alt={artist.name} width="100" />
                            <br />
                            <button onClick={() => handleDelete(artist._id)}>Delete</button>
                        </li>
                    ))
                ) : (
                    <p>No artists found.</p>
                )}
            </ul>
        </div>
    );
};

export default ManageArtists;
