import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AddArtist = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        bio: '',
        category: '',
        profilePicture: null, // For storing the uploaded file
    });
    const { token } = useAuth();
    const navigate = useNavigate();

    // Handle input changes for text fields
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle file input change
    const handleFileChange = (e) => {
        setFormData({ ...formData, profilePicture: e.target.files[0] }); // Store the selected file
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Basic validation
        if (!formData.name || !formData.email || !formData.category || !formData.profilePicture) {
            alert('All fields are required.');
            return;
        }

        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(formData.email)) {
            alert('Please enter a valid email address.');
            return;
        }

        try {
            const formDataToSend = new FormData(); // Use FormData to send files
            formDataToSend.append('name', formData.name);
            formDataToSend.append('email', formData.email);
            formDataToSend.append('bio', formData.bio);
            formDataToSend.append('category', formData.category);
            formDataToSend.append('profilePicture', formData.profilePicture); // Append the file

            console.log('Adding artist with data:', formData);

            // Send POST request to add artist
            const response = await axios.post('/api/admin/artists', formDataToSend, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data', // Important for file uploads
                },
            });

            console.log('Artist added successfully:', response.data);
            alert('Artist added successfully!');
            navigate('/admin/artists'); // Redirect to artists list
        } catch (err) {
            console.error('Error adding artist:', err.response?.data || err.message);
            alert('Failed to add artist. Please try again.');
        }
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.heading}>Add New Artist</h2>

            {/* Form for adding an artist */}
            <form onSubmit={handleSubmit} encType="multipart/form-data" style={styles.form}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    aria-label="Artist Name"
                    style={styles.input}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    aria-label="Artist Email"
                    style={styles.input}
                />
                <textarea
                    name="bio"
                    placeholder="Bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                    aria-label="Artist Bio"
                    style={styles.textarea}
                />
                <input
                    type="text"
                    name="category"
                    placeholder="Category"
                    value={formData.category}
                    onChange={handleInputChange}
                    required
                    aria-label="Artist Category"
                    style={styles.input}
                />
                <label htmlFor="profilePicture" style={styles.label}>
                    Profile Picture:
                </label>
                <input
                    type="file"
                    id="profilePicture"
                    name="profilePicture"
                    onChange={handleFileChange}
                    required
                    aria-label="Upload Profile Picture"
                    style={styles.fileInput}
                />
                <button type="submit" style={styles.button}>
                    Add Artist
                </button>
            </form>
        </div>
    );
};

// Inline Styles
const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
        backgroundColor: 'var(--bg-color)',
        color: 'var(--text-color)',
        border:'1px',
    },
    heading: {
        fontSize: '2rem',
        marginBottom: '20px',
        color: 'var(--text-color)',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        maxWidth: '400px',
        gap: '15px',
    },
    input: {
        padding: '10px',
        fontSize: '1rem',
        border: '1px solid var(--border-color)',
        borderRadius: '5px',
        backgroundColor: 'var(--input-bg-color)',
        color: 'var(--text-color)',
    },
    textarea: {
        padding: '10px',
        fontSize: '1rem',
        border: '1px solid var(--border-color)',
        borderRadius: '5px',
        resize: 'vertical',
        minHeight: '80px',
        backgroundColor: 'var(--input-bg-color)',
        color: 'var(--text-color)',
    },
    label: {
        fontSize: '1rem',
        fontWeight: 'bold',
        color: 'var(--text-color)',
    },
    fileInput: {
        padding: '10px',
        fontSize: '1rem',
        border: '1px solid var(--border-color)',
        borderRadius: '5px',
        backgroundColor: 'var(--input-bg-color)',
        color: 'var(--text-color)',
    },
    button: {
        padding: '10px',
        backgroundColor: 'var(--primary-color)',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '1rem',
        transition: 'background-color 0.3s ease',
    },
};

export default AddArtist;
