import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AddArtist = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        bio: '',
        category: '',
        profilePicture: null, // For storing the uploaded file
    });
    const { token } = useAuth();
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        setFormData({ ...formData, profilePicture: e.target.files[0] }); // Store the selected file
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formDataToSend = new FormData(); // Use FormData to send files
            formDataToSend.append('name', formData.name);
            formDataToSend.append('email', formData.email);
            formDataToSend.append('bio', formData.bio);
            formDataToSend.append('category', formData.category);
            formDataToSend.append('profilePicture', formData.profilePicture); // Append the file

            console.log('Adding artist with data:', formData);

            // Send POST request to add artist
            const response = await axios.post('/api/admin/artists', formDataToSend, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data', // Important for file uploads
                },
            });

            console.log('Artist added successfully:', response.data);
            alert('Artist added successfully!');
            navigate('/admin/artists'); // Redirect to artists list
        } catch (err) {
            console.error('Error adding artist:', err.response?.data || err.message);
            alert('Failed to add artist. Please try again.');
        }
    };

    return (
        <div>
            <h2>Add New Artist</h2>

            {/* Form for adding an artist */}
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <input
                    type="text"
                    name="nasame"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                />
                <textarea
                    name="bio"
                    placeholder="Bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="category"
                    placeholder="Category"
                    value={formData.category}
                    onChange={handleInputChange}
                    required
                />
                <label htmlFor="profilePicture">Profile Picture:</label>
                <input
                    type="file"
                    id="profilePicture"
                    name="profilePicture"
                    onChange={handleFileChange}
                    required
                />
                <button type="submit">Add Artist</button>
            </form>
        </div>
    );
};

export default AddArtist;
