import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AddArtist = () => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        image: '',
    });
    const { token } = useAuth();
    const navigate = useNavigate();

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            console.log('Adding artist with data:', formData);

            // Send POST request to add artist
            const response = await axios.post('/api/admin/artists', formData, {
                headers: { Authorization: `Bearer ${token}` },
            });

            console.log('Artist added successfully:', response.data);
            alert('Artist added successfully!');
            navigate('/admin/artists'); // Redirect to artists list
        } catch (err) {
            console.error('Error adding artist:', err.response?.data || err.message);
            alert('Failed to add artist. Please try again.');
        }
    };

    return (
        <div>
            <h2>Add New Artist</h2>

            {/* Form for adding an artist */}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="description"
                    placeholder="Description"
                    value={formData.description}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="image"
                    placeholder="Image URL"
                    value={formData.image}
                    onChange={handleInputChange}
                    required
                />
                <button type="submit">Add Artist</button>
            </form>
        </div>
    );
};

export default AddArtist;
