import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const ManageArtists = () => {
    const [artists, setArtists] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        image: '',
    });
    const [editingId, setEditingId] = useState(null);
    const { token } = useAuth();

    // Fetch all artists using useCallback
    const fetchArtists = useCallback(async () => {
        try {
            const response = await axios.get('/api/admin/artists', {
                headers: { Authorization: `Bearer ${token}` },
            });
            console.log('API Response:', response.data); // Log API response
            setArtists(response.data);
        } catch (err) {
            console.error('Error fetching artists:', err.response?.data || err.message);
        }
    }, [token]);

    useEffect(() => {
        fetchArtists();
    }, [fetchArtists]);

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Add or update an artist
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingId) {
                // Update existing artist
                await axios.put(`/api/admin/artists/${editingId}`, formData, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setEditingId(null);
            } else {
                // Add new artist
                await axios.post('/api/admin/artists', formData, {
                    headers: { Authorization: `Bearer ${token}` },
                });
            }

            setFormData({ name: '', description: '', image: '' });
            fetchArtists(); // Refresh the list
        } catch (err) {
            console.error('Error submitting form:', err.response?.data || err.message);
        }
    };

    // Delete an artist
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/admin/artists/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchArtists(); // Refresh the list
        } catch (err) {
            console.error('Error deleting artist:', err.response?.data || err.message);
        }
    };

    // Edit an artist
    const handleEdit = (artist) => {
        setFormData({
            name: artist.name,
            description: artist.description,
            image: artist.image,
        });
        setEditingId(artist._id);
    };

    return (
        <div>
            <h2>Manage Artists</h2>

            {/* Form for adding/updating artists */}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="description"
                    placeholder="Description"
                    value={formData.description}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="text"
                    name="image"
                    placeholder="Image URL"
                    value={formData.image}
                    onChange={handleInputChange}
                    required
                />
                <button type="submit">{editingId ? 'Update Artist' : 'Add Artist'}</button>
            </form>

            {/* List of artists */}
            <ul>
                {artists.length > 0 ? (
                    artists.map((artist) => (
                        <li key={artist._id}>
                            <strong>{artist.name}</strong>: {artist.description}
                            <br />
                            <img src={artist.image} alt={artist.name} width="100" />
                            <br />
                            <button onClick={() => handleEdit(artist)}>Edit</button>
                            <button onClick={() => handleDelete(artist._id)}>Delete</button>
                        </li>
                    ))
                ) : (
                    <p>No artists found.</p>
                )}
            </ul>
        </div>
    );
};

export default ManageArtists;
