import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const PlaceOrder = () => {
    const [artists, setArtists] = useState([]);
    const [formData, setFormData] = useState({
        artistId: '',
        date: '',
        location: '',
        totalPrice: 0,
    });
    const { token } = useAuth();
    const navigate = useNavigate();

    // Fetch available artists
    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/artists');
                setArtists(response.data);
            } catch (err) {
                console.error('Error fetching artists:', err.response?.data || err.message);
            }
        };
        fetchArtists();
    }, []);

    // Handle form input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle placing an order
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/orders', formData, {
                headers: { Authorization: `Bearer ${token}` },
            });

            alert('Order placed successfully!');
            navigate('/user/orders'); // Redirect to user's orders page
        } catch (err) {
            console.error('Error placing order:', err.response?.data || err.message);
            alert('Failed to place order. Please try again.');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Place an Order</h2>

            <form onSubmit={handleSubmit} style={styles.form}>
                {/* Artist Selection */}
                <label htmlFor="artistId" style={styles.label}>
                    Select Artist:
                </label>
                <select
                    id="artistId"
                    name="artistId"
                    value={formData.artistId}
                    onChange={handleChange}
                    required
                    style={styles.select}
                >
                    <option value="">-- Select an Artist --</option>
                    {artists.map((artist) => (
                        <option key={artist._id} value={artist._id}>
                            {artist.name} - ₮{artist.pricePerHour}/hour
                        </option>
                    ))}
                </select>

                {/* Date */}
                <label htmlFor="date" style={styles.label}>
                    Date:
                </label>
                <input
                    type="date"
                    id="date"
                    name="date"
                    value={formData.date}
                    onChange={handleChange}
                    required
                    style={styles.input}
                />

                {/* Location */}
                <label htmlFor="location" style={styles.label}>
                    Location:
                </label>
                <input
                    type="text"
                    id="location"
                    name="location"
                    placeholder="Enter location"
                    value={formData.location}
                    onChange={handleChange}
                    required
                    style={styles.input}
                />

                {/* Total Price */}
                <label htmlFor="totalPrice" style={styles.label}>
                    Total Price (₮):
                </label>
                <input
                    type="number"
                    id="totalPrice"
                    name="totalPrice"
                    placeholder="Enter total price"
                    value={formData.totalPrice}
                    onChange={handleChange}
                    required
                    style={styles.input}
                />

                {/* Submit Button */}
                <button type="submit" style={styles.button}>
                    Place Order
                </button>
            </form>
        </div>
    );
};

// Inline Styles
const styles = {
    container: {
        padding: '20px',
        fontFamily: 'Arial, sans-serif',
        backgroundColor: 'var(--bg-color)',
        color: 'var(--text-color)',
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '15px',
    },
    label: {
        fontSize: '1rem',
        fontWeight: 'bold',
        color: 'var(--text-color)',
    },
    select: {
        padding: '10px',
        fontSize: '1rem',
        border: '1px solid var(--border-color)',
        borderRadius: '5px',
        backgroundColor: 'var(--input-bg-color)',
        color: 'var(--text-color)',
    },
    input: {
        padding: '10px',
        fontSize: '1rem',
        border: '1px solid var(--border-color)',
        borderRadius: '5px',
        backgroundColor: 'var(--input-bg-color)',
        color: 'var(--text-color)',
    },
    button: {
        padding: '10px',
        backgroundColor: 'var(--primary-color)',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
};

export default PlaceOrder;
