import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const ManageOrders = () => {
    const [orders, setOrders] = useState([]);
    const { token } = useAuth();

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get('/api/orders', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setOrders(response.data);
            } catch (err) {
                console.error('Error fetching orders:', err.response?.data || err.message);
            }
        };
        fetchOrders();
    }, [token]);

    return (
        <div style={styles.container}>
            <h2>Manage All Orders</h2>
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th style={styles.th}>User</th>
                        <th style={styles.th}>Artist</th>
                        <th style={styles.th}>Date</th>
                        <th style={styles.th}>Location</th>
                        <th style={styles.th}>Total Price (â‚®)</th>
                        <th style={styles.th}>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.length > 0 ? (
                        orders.map((order) => (
                            <tr key={order._id} style={styles.tr}>
                                <td style={styles.td}>{order.userId.name} ({order.userId.email})</td>
                                <td style={styles.td}>{order.artistId.name}</td>
                                <td style={styles.td}>{new Date(order.date).toLocaleDateString()}</td>
                                <td style={styles.td}>{order.location}</td>
                                <td style={styles.td}>â‚®{order.totalPrice}</td>
                                <td style={styles.td}>{order.status}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" style={styles.noData}>
                                No orders found.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

const styles = {
    container: {
        backgroundColor: 'var(--bg-color)',
        padding: '20px',
        borderRadius: '8px',
        boxShadow: 'rgba(0, 0, 0, 0.1) 0px 2px 5px',
    },
    heading: {
        fontSize: '2rem',
        marginBottom: '20px',
        color: 'var(--text-color)',
    },
    addButton: {
        padding: '10px 20px',
        backgroundColor: '#2ecc71',
        color: '#fff',
        border: 'none',
        borderRadius: '50px',
        cursor: 'pointer',
        marginBottom: '20px',
        transition: 'background-color 0.3s',
    },
    table: {
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: '20px',
    },
    th: {
        backgroundColor: 'var(--primary-color)',
        color: '#fff',
        padding: '10px',
        textAlign: 'left',
    },
    tr: {
        borderBottom: '1px solid var(--border-color)',
    },
    td: {
        padding: '10px',
        color: 'var(--text-color)',
    },
    deleteButton: {
        padding: '5px 10px',
        backgroundColor: '#e74c3c',
        color: '#fff',
        border: 'none',
        borderRadius: '50px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
    noData: {
        textAlign: 'center',
        padding: '20px',
        color: 'var(--secondary-text-color)',
    },
    loadingMessage: {
        textAlign: 'center',
        marginTop: '20px',
        color: 'var(--secondary-text-color)',
    },
};

export default ManageOrders;
