import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Artists.css'; // Import the updated CSS file

const Artists = () => {
    const [artists, setArtists] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/artists');
                setArtists(response.data);
                setLoading(false);
            } catch (err) {
                console.error(err.response?.data || err.message);
                setLoading(false);
            }
        };

        fetchArtists();
    }, []);

    if (loading) return <p>Loading artists...</p>;

    return (
        <div className="artists-container">
            <h2 style={{ textAlign: 'center', marginBottom: '200px', fontSize: '2rem', color: '#333333' }}>
                Уран бүтээлчид
            </h2>
            {artists.length > 0 ? (
                artists.map((artist) => (
                    <div key={artist._id} className="artist-card">
                        <img
                            src={artist.profilePicture || '/default-avatar.png'}
                            alt={`${artist.name}'s profile`}
                            onError={(e) => {
                                e.target.src = '/default-avatar.png'; // Fallback image
                            }}
                        />
                        <h3>{artist.name}</h3>
                        <p>{artist.bio || 'No bio available.'}</p>
                    </div>
                ))
            ) : (
                <p>No artists found.</p>
            )}
        </div>
    );
};

export default Artists;
