import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddArtist = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        bio: '',
        category: '',
        profilePicture: null,
    });
    const [preview, setPreview] = useState(null); // For profile picture preview
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'profilePicture') {
            const file = e.target.files[0];
            setFormData((prevData) => ({
                ...prevData,
                [name]: file,
            }));

            if (file) {
                const reader = new FileReader();
                reader.onloadend = () => {
                    setPreview(reader.result); // Set the preview URL
                };
                reader.readAsDataURL(file);
            } else {
                setPreview(null); // Clear the preview if no file is selected
            }
        } else {
            setFormData((prevData) => ({
                ...prevData,
                [name]: value,
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
            const formDataToSend = new FormData(); // Create FormData object
            formDataToSend.append('name', formData.name || '');
            formDataToSend.append('email', formData.email || '');
            formDataToSend.append('bio', formData.bio || '');
            formDataToSend.append('category', formData.category || '');
            if (formData.profilePicture) {
                formDataToSend.append('profilePicture', formData.profilePicture); // Append the file
            }
    
            console.log('Form Data:', Object.fromEntries(formDataToSend.entries())); // Log form data
    
            const token = localStorage.getItem('token'); // Retrieve token from localStorage
            console.log('Token:', token); // Log the token being sent
    
            await axios.post('/api/artists', formDataToSend, {
                headers: {
                    'Content-Type': 'multipart/form-data', // Important for file uploads
                    Authorization: `Bearer ${token}`, // Include token for authentication
                },
            });
    
            alert('Artist added successfully!');
            navigate('/admin/artists'); // Redirect to manage artists page
        } catch (err) {
            console.error('Error adding artist:', err.response?.data || err.message); // Log the full error
            alert('Failed to add artist. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Add Artist</h2>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div style={{ marginBottom: '15px' }}>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        placeholder="Enter artist's name"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        placeholder="Enter artist's email"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Bio:</label>
                    <textarea
                        name="bio"
                        value={formData.bio}
                        onChange={handleChange}
                        placeholder="Enter artist's bio"
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Category:</label>
                    <input
                        type="text"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        placeholder="Enter artist's category"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Profile Picture:</label>
                    <input
                        type="file"
                        name="profilePicture"
                        accept="image/*"
                        onChange={handleChange}
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                    {preview && (
                        <img
                            src={preview}
                            alt="Profile Preview"
                            style={{
                                width: '100px',
                                height: '100px',
                                objectFit: 'cover',
                                marginTop: '10px',
                            }}
                        />
                    )}
                </div>
                <button
                    type="submit"
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#2c3e50',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    Add Artist
                </button>
            </form>
        </div>
    );
};

export default AddArtist;
