import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddArtist = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        category: '',
        profilePicture: '',
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Send the form data to the backend
            await axios.post('/api/artists/add', formData);

            alert('Artist added successfully!');
            navigate('/admin/artists'); // Redirect to the manage artists page
        } catch (err) {
            console.error('Error adding artist:', err.response?.data || err.message);
            alert('Failed to add artist. Please try again.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Add Artist</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        placeholder="Enter artist's name"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        placeholder="Enter artist's email"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Category:</label>
                    <input
                        type="text"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        placeholder="Enter artist's category"
                        required
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label>Profile Picture URL:</label>
                    <input
                        type="url"
                        name="profilePicture"
                        value={formData.profilePicture}
                        onChange={handleChange}
                        placeholder="Enter profile picture URL"
                        style={{ display: 'block', width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>
                <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#2c3e50', color: '#fff', border: 'none', cursor: 'pointer' }}>
                    Add Artist
                </button>
            </form>
        </div>
    );
};

export default AddArtist;
