import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const useAuth = () => {
    return React.useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(null);
    const [user, setUser] = useState(null);

    // Load token and user data from localStorage on app initialization
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            console.log('Token found in localStorage:', storedToken);
            setToken(storedToken); // Restore token

            // Fetch user data from the backend
            const fetchUserData = async () => {
                try {
                    console.log('Fetching user data from /api/auth/me...');
                    const response = await axios.get('/api/auth/me', {
                        headers: { Authorization: `Bearer ${storedToken}` },
                    });
                    console.log('User data fetched successfully:', response.data);
                    setUser(response.data); // Restore user data
                } catch (err) {
                    console.error('Failed to fetch user data:', err.response?.data || err.message);
                    logout(); // Clear invalid session
                }
            };

            fetchUserData();
        } else {
            console.log('No token found in localStorage.');
        }
    }, []);

    const login = (userData) => {
        console.log('Logging in user:', userData.user);
        setToken(userData.token);
        setUser(userData.user);
        localStorage.setItem('token', userData.token); // Save token to localStorage
    };

    const logout = () => {
        console.log('Logging out user...');
        setToken(null);
        setUser(null);
        localStorage.removeItem('token'); // Remove token from localStorage
    };

    return (
        <AuthContext.Provider value={{ token, user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
