import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

// Custom hook to simplify access to the AuthContext
export const useAuth = () => {
    return React.useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(null);
    const [user, setUser] = useState(null);

    // Restore authentication state on page load
    useEffect(() => {
        const verifyToken = async () => {
            const storedToken = localStorage.getItem('token');
            if (storedToken) {
                try {
                    // Verify the token and fetch user data
                    const response = await axios.get('/api/auth/me', {
                        headers: { Authorization: `Bearer ${storedToken}` },
                    });
                    setToken(storedToken); // Restore token in state
                    setUser(response.data); // Restore user data in state
                } catch (err) {
                    console.error('Token verification failed:', err.message);
                    logout(); // Log out if token is invalid
                }
            }
        };

        verifyToken();
    }, []);

    // Function to log in the user
    const login = async (userData) => {
        const { token, user } = userData;
        setToken(token); // Update state with the token
        setUser(user); // Update state with the user data
        localStorage.setItem('token', token); // Save token to localStorage
    };

    // Function to log out the user
    const logout = () => {
        setToken(null); // Clear token from state
        setUser(null); // Clear user data from state
        localStorage.removeItem('token'); // Remove token from localStorage
    };

    return (
        <AuthContext.Provider value={{ token, user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
