import React, { useContext } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { ThemeContext } from '../context/ThemeContext';
import './Navbar.css'; // Import the external CSS file

const Navbar = () => {
    const { user, logout } = useContext(AuthContext);
    const { darkMode, toggleDarkMode } = useContext(ThemeContext); // Access dark mode state
    const navigate = useNavigate();

    // Handle logout
    const handleLogout = () => {
        logout(); // Clear user data from context
        navigate('/login'); // Redirect to login page
    };

    return (
        <nav className={`navbar ${darkMode ? 'dark-mode' : ''}`}>
            {/* Logo */}
            <div className="navbar-logo">
                <NavLink to="/">
                    <img src="/logo.png" alt="–ê—Ä—Ç–∏—Å—Ç –ë—É—É–¥–∞–ª" />
                </NavLink>
            </div>

            {/* Navigation Links */}
            <ul className="navbar-links">
                <li>
                    <NavLink to="/" activeClassName="active-link">–≠—Ö–ª—ç–ª</NavLink>
                </li>
                <li>
                    <NavLink to="/about" activeClassName="active-link">–ë–∏–¥–Ω–∏–π —Ç—É—Ö–∞–π</NavLink>
                </li>
                <li>
                    <NavLink to="/artists" activeClassName="active-link">–£—Ä–∞–Ω –±“Ø—Ç—ç—ç–ª—á–∏–¥</NavLink>
                </li>
                <li>
                    <NavLink to="/booking" activeClassName="active-link">–ó–∞—Ö–∏–∞–ª–≥–∞ ”©–≥”©—Ö</NavLink>
                </li>
                <li>
                    <NavLink to="/contact" activeClassName="active-link">–•–æ–ª–±–æ–æ –±–∞—Ä–∏—Ö</NavLink>
                </li>
            </ul>

            {/* User Info and Logout/Login Button */}
            <div className="auth-section">
                {user ? (
                    <div className="auth-link">
                        <span>–°–∞–π–Ω —É—É, {user?.name || '–•—ç—Ä—ç–≥–ª—ç–≥—á'}</span>
                        <button onClick={handleLogout} className="logout-button">–ì–∞—Ä–∞—Ö</button>
                    </div>
                ) : (
                    <NavLink to="/login" className="auth-link">–ù—ç–≤—Ç—Ä—ç—Ö</NavLink>
                )}
            </div>

            {/* Dark Mode Toggle Button */}
            <div className="dark-mode-container">
                <button
                    className="dark-mode-toggle"
                    onClick={toggleDarkMode}
                    title={darkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
                >
                    {darkMode ? '‚òÄÔ∏è' : 'üåô'}
                </button>
            </div>
        </nav>
    );
};

export default Navbar;
