import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';

const ManageArtists = () => {
    const [artists, setArtists] = useState([]);
    const { token } = useAuth();

    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/admin/artists', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setArtists(response.data);
            } catch (err) {
                console.error('Error fetching artists:', err.response?.data || err.message);
            }
        };
        fetchArtists();
    }, [token]);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/admin/artists/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setArtists((prevArtists) => prevArtists.filter((artist) => artist._id !== id));
        } catch (err) {
            console.error('Error deleting artist:', err.response?.data || err.message);
        }
    };

    return (
        <div>
            <h2>Manage Artists</h2>
            <Link to="/admin/add-artist">
                <button style={styles.addButton}>Add New Artist</button>
            </Link>

            {/* Table for Artists */}
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th style={styles.th}>Name</th>
                        <th style={styles.th}>Email</th>
                        <th style={styles.th}>Category</th>
                        <th style={styles.th}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {artists.length > 0 ? (
                        artists.map((artist) => (
                            <tr key={artist._id} style={styles.tr}>
                                <td style={styles.td}>{artist.name}</td>
                                <td style={styles.td}>{artist.email}</td>
                                <td style={styles.td}>{artist.category}</td>
                                <td style={styles.td}>
                                    <button onClick={() => handleDelete(artist._id)} style={styles.deleteButton}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4" style={styles.noData}>
                                No artists found.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

// Inline Styles for ManageArtists
const styles = {
    addButton: {
        padding: '10px 20px',
        backgroundColor: '#2ecc71',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        marginBottom: '20px',
        transition: 'background-color 0.3s',
    },
    addButtonHover: {
        backgroundColor: '#27ae60',
    },
    table: {
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: '20px',
    },
    th: {
        backgroundColor: '#3498db',
        color: '#fff',
        padding: '10px',
        textAlign: 'left',
    },
    tr: {
        borderBottom: '1px solid #ddd',
    },
    td: {
        padding: '10px',
    },
    deleteButton: {
        padding: '5px 10px',
        backgroundColor: '#e74c3c',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        transition: 'background-color 0.3s',
    },
    deleteButtonHover: {
        backgroundColor: '#c0392b',
    },
    noData: {
        textAlign: 'center',
        padding: '20px',
        color: '#888',
    },
};

export default ManageArtists;
