import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
    const [artists, setArtists] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch all artists
    useEffect(() => {
        const fetchArtists = async () => {
            try {
                const response = await axios.get('/api/artists');
                setArtists(response.data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching artists:', err.response?.data || err.message);
                setError('Failed to load artists.');
                setLoading(false);
            }
        };

        fetchArtists();
    }, []);

    // Delete an artist
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/artists/${id}`);
            setArtists((prevArtists) => prevArtists.filter((artist) => artist._id !== id));
        } catch (err) {
            console.error('Error deleting artist:', err.response?.data || err.message);
            alert('Failed to delete artist.');
        }
    };

    if (loading) return <p>Loading artists...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div>
            <h2>Admin Dashboard</h2>
            <h3>Manage Artists</h3>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Category</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {artists.map((artist) => (
                        <tr key={artist._id}>
                            <td>{artist.name}</td>
                            <td>{artist.email}</td>
                            <td>{artist.category}</td>
                            <td>
                                <button onClick={() => handleDelete(artist._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
